'use strict';
var logger = require('log4js').getLogger('Sensor');

function initNetworks() {
  var futureNetwork;

  try {
    futureNetwork = require('./network/future');
  } catch (e) {
    logger.error('[futuretek] init networks error', e);
  }

  return {
    future: futureNetwork
  };
}

function initDrivers() {
  var futureSensor, futureActuator;

  try {
    futureSensor = require('./driver/futureSensor');
    futureActuator = require('./driver/futureActuator');
  } catch(e) {
    logger.error('[futuretek] init drivers error', e);
  }

  return {
    futureSensor: futureSensor,
    futureActuator: futureActuator
  };
}

module.exports = {
  networks: ['future'],
  drivers: {
    futureSensor: ['futureDi', 'futureTemp', 'futureHumi', 'futureRl', 'futureGas', 'futurePower', 'futureDust'],
    futureActuator: ['futureDo']
  },
  initNetworks: initNetworks,
  initDrivers: initDrivers
};
